const sourceOfEvent = declared => declared['@source'] || (declared._service && declared._service['@event.source'])
const srvNameOfEvent = declared => declared._service.name
const isDeclared = event => typeof event === 'object' && event.kind === 'event'
const defaultSource = '/default/sap/cap'

module.exports = {
  sourceOfEvent,
  srvNameOfEvent,
  isDeclared,
  defaultSource
}
